mapper.insert:
insert into six_plan (plan_name, plan_time, plan_per,
plan_status, plan_content, plan_type,
plan_year, plan_season, plan_month,
plan_week, parent_id, source_id
)
values (#{planName,jdbcType=VARCHAR}, #{planTime,jdbcType=INTEGER}, #{planPer,jdbcType=INTEGER},
#{planStatus,jdbcType=INTEGER}, #{planContent,jdbcType=VARCHAR}, #{planType,jdbcType=INTEGER},
#{planYear,jdbcType=INTEGER}, #{planSeason,jdbcType=INTEGER}, #{planMonth,jdbcType=INTEGER},
#{planWeek,jdbcType=INTEGER}, #{parentId,jdbcType=INTEGER}, #{sourceId,jdbcType=INTEGER}
)

mapper.select:
select

,(CASE plan_status WHEN 100 THEN '未完成' WHEN 300 THEN '已完成' WHEN 400
THEN '进行中' END) plan_status_value
from six_plan
where 1=1

    AND plan_name = #{planName,jdbcType=VARCHAR}


    AND plan_time = #{planTime,jdbcType=INTEGER}


    AND plan_per = #{planPer,jdbcType=INTEGER}


    AND plan_status = #{planStatus,jdbcType=INTEGER}


    AND plan_content = #{planContent,jdbcType=VARCHAR}


    AND plan_type = #{planType,jdbcType=INTEGER}


    AND plan_year = #{planYear,jdbcType=INTEGER}


    AND plan_season = #{planSeason,jdbcType=INTEGER}


    AND plan_month = #{planMonth,jdbcType=INTEGER}


    AND plan_week = #{planWeek,jdbcType=INTEGER}


    AND parent_id = #{parentId,jdbcType=INTEGER}


    AND source_id = #{sourceId,jdbcType=INTEGER}

order BY plan_year DESC,plan_season desc,plan_month DESC ,plan_week DESC
