mapper.select:
select

from six_plan
where 1=1


        plan_name = #{planName,jdbcType=VARCHAR}


        plan_time = #{planTime,jdbcType=INTEGER}


        plan_per = #{planPer,jdbcType=INTEGER}


        plan_status = #{planStatus,jdbcType=INTEGER}


        plan_content = #{planContent,jdbcType=VARCHAR}


        plan_type = #{planType,jdbcType=INTEGER}


        plan_date = #{planDate,jdbcType=INTEGER}


        parent_id = #{parentId,jdbcType=INTEGER}


        source_id = #{sourceId,jdbcType=INTEGER}


order BY plan_type DESC, plan_date DESC
