mapper.update:
update seis_plan
plan_name = #{planName, jdbcType=VARCHAR}, plan_time = #{planTime, jdbcType=VARCHAR}, plan_per = #{planPer, jdbcType=VARCHAR}, plan_status = #{planStatus, jdbcType=VARCHAR}, plan_content = #{planContent, jdbcType=VARCHAR}, plan_type = #{planType, jdbcType=VARCHAR}, plan_year = #{planYear, jdbcType=VARCHAR}, plan_season = #{planSeason, jdbcType=VARCHAR}, plan_month = #{planMonth, jdbcType=VARCHAR}, plan_week = #{planWeek, jdbcType=VARCHAR}, parent_id = #{parentId, jdbcType=INTEGER}, source_id = #{sourceId, jdbcType=INTEGER},
where id = #{id,jdbcType=INTEGER}

mapper.select:
select

,(CASE plan_status WHEN 100 THEN '未完成' WHEN 300 THEN '已完成' WHEN 400
THEN '进行中' END) plan_status_name
from seis_plan
where 1=1
 AND plan_name    = #{planName,    jdbcType=VARCHAR} 
 AND plan_time    = #{planTime,    jdbcType=VARCHAR} 
 AND plan_per     = #{planPer,     jdbcType=VARCHAR} 
 AND plan_status  = #{planStatus,  jdbcType=VARCHAR} 
 AND plan_content = #{planContent, jdbcType=VARCHAR} 
 AND plan_type    = #{planType,    jdbcType=VARCHAR} 
 AND plan_year    = #{planYear,    jdbcType=VARCHAR} 
 AND plan_season  = #{planSeason,  jdbcType=VARCHAR} 
 AND plan_month   = #{planMonth,   jdbcType=VARCHAR} 
 AND plan_week    = #{planWeek,    jdbcType=VARCHAR} 
 AND parent_id    = #{parentId,    jdbcType=INTEGER} 
 AND source_id    = #{sourceId,    jdbcType=INTEGER} 
order BY plan_year DESC,plan_type desc,id DESC
